mapper.update:
update `user` a
a.username = #{username}, a.unionid = #{unionid}, a.openid = #{openid}, a.nickname = #{nickname}, a.password = #{password}, a.telphone = #{phone}, a.avatar = #{avatar}, a.is_disable = #{is_disable}, a.money = #{money}, a.deposit = #{deposit}, a.session_key = #{session_key}, a.status = #{status}, a.gender = #{gender},
where id = #{user_id}

mapper.select:
select a.* from `user` a

    and a.id = #{user_id}
    and a.username = #{username}
    and a.openid = #{openid}
    and a.unionid = #{unionid}
    and a.type = #{type}
    and a.telphone = #{telphone}
    and a.is_disable = #{is_disable}
    and a.status = #{status}
    and a.gender = #{gender}
